import * as types from "./actionTypes";

const initialState = {
  fetching: false,
  failed: false,
  comments: []
};

function reducer(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case types.REQUEST_COMMENTS:
      return {
        ...state,
        fetching: true,
        failed: false
      };
  }
}
