import * as types from "./actionTypes";

const initialState = {
  fetching: false,
  failed: false,
  posts: []
};

function reducer(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case types.REQUEST_COMMENTS:
      return {
        ...state,
        fetching: true,
        failed: false
      };
    case types.FETCH_COMMENTS:
      return {
        ...state,
        fetching: false,
        failed: false,
        comments: payload
      };
    default:
      return state;
  }
}
export default reducer;
