import Http from "utils/Http";
import {
  requestComments,
  fetchComments,
  requestCommentsFailed
} from "./store/actionCreator";
import { response } from "express";

function fetchPosts() {
  return dispatch => {
    dispatch(requestComments());
    return Http.get("/posts").then(response => {
      fetchComments(response.data);
    });
  };
}
